import { Component } from '@angular/core';
import { FormGroup } from '@angular/forms';
import { FormControl } from '@angular/forms';
import { Router } from '@angular/router';

@Component({
  selector: 'app-admin-login',
  templateUrl: './admin-login.component.html',
  styleUrls: ['./admin-login.component.css']
})
export class AdminLoginComponent {
  adminRef = new FormGroup({
    email:new FormControl(),
    password:new FormControl()
  });

}
<html>
<head>
    <!-- Bootstrap Implementation -->
    <link href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.2/dist/css/bootstrap.min.css" rel="stylesheet" integrity="sha384-T3c6CoIi6uLrA9TneNEoa7RxnatzjcDSCmG1MXxSR1GAsXEV/Dwwykc2MPK8M2HN" crossorigin="anonymous"/>
    <meta name="viewport" content="width=device-width, initial-scale=1">
</head>
<body>
<br/><div>
    <h3>Welcome back, Admin!</h3><br/>
    <p class="smallText">Please enter your credentials below:<p>
    <form [formGroup]="adminRef" (ngSubmit)="checkAdmin()">
        <input type="email" formControlName="email" placeholder="Email"><br/>
        <input type="password" formControlName="password" placeholder="Password"><br/>
        <input type="submit" value="Sign In" class="btn btn-primary">
    </form>
</div>
</body>
</html>
