import { Component } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';
import { Router } from '@angular/router';
import { MeetingrequestService } from '../services/meetingrequest.service';
import { Meetingrequest } from '../classes/meetingrequest';

@Component({
  selector: 'app-client-meetings',
  templateUrl: './client-meetings.component.html',
  styleUrls: ['./client-meetings.component.css']
})
export class ClientMeetingsComponent {
  newReq = new FormGroup({
    name:new FormControl(),
    email:new FormControl(),
    participants:new FormControl(),
    date:new FormControl(),
    topic:new FormControl()
  });

  meetingrequests:Array<Meetingrequest> = [];

  constructor(
    public requestService:MeetingrequestService,
    public router:Router) {}

  newRequest():void {
    let request = this.newReq.value;
    this.requestService.storeRequest(request).subscribe({
      next:(result:any)=> {
        this.meetingrequests = result;
      },
      error:(error:any)=> {
        console.log(error);
      },
      complete:()=> {
        alert("Request Submitted!");
        console.log("done!");
      }
    });
    this.newReq.reset();
  }

  clearForm()
}
<html>
    <head>
        <!-- Bootstrap Implementation -->
        <link href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.2/dist/css/bootstrap.min.css" rel="stylesheet" integrity="sha384-T3c6CoIi6uLrA9TneNEoa7RxnatzjcDSCmG1MXxSR1GAsXEV/Dwwykc2MPK8M2HN" crossorigin="anonymous"/>
        <meta name="viewport" content="width=device-width, initial-scale=1">
    </head>
    <body>
        <div>
            <div align="center">
            <h3>Meeting Requests</h3>
            </div><br/>

        <div align="center">
        <br/>
        <h4 style="color:white">Meeting Request Form</h4>
        <form [formGroup]="newReq" (ngSubmit)="newRequest()">
            <label for="name">Client Name</label><br/>
            <input type="text" formControlName="name"><br/>
            <label for="email">Client Email</label><br/>
            <input type="text" formControlName="email"><br/>
            <label for="participants">Number of Participants</label><br/>
            <input type="text" formControlName="participants"><br/>
            <label for="date">Date and Time</label><br/>
            <input type="text" formControlName="date"><br/>
            <label for="topic">Meeting Topic</label><br/>
            <input type="text" formControlName="topic"><br/>
            <input class="btn btn-success" type="submit" value="Submit"> 
            <button class="btn btn-warning" (click)="clearForm()">Clear</button>
        </form>
        </div>
        </div>
    </body>
</html>
