import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-admin-dashboard',
  templateUrl: './admin-dashboard.component.html',
  styleUrls: ['./admin-dashboard.component.css']
})
export class AdminDashboardComponent {
  msgTag:boolean = true;
  constructor(public router:Router) {}
  checkChild() {
    let currRoute:string = this.router.url;
    if (currRoute !== "/admin-dashboard") {
      this.msgTag = false;
    }
    else {
      this.msgTag = true;
    }
  }
}
<head>
    <!-- Bootstrap Implementation -->
    <link href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.2/dist/css/bootstrap.min.css" rel="stylesheet" integrity="sha384-T3c6CoIi6uLrA9TneNEoa7RxnatzjcDSCmG1MXxSR1GAsXEV/Dwwykc2MPK8M2HN" crossorigin="anonymous"/>
    <meta name="viewport" content="width=device-width, initial-scale=1">
</head>

<body>
    <div *ngIf="msgTag" id="msg">
        <h2>Welcome to your portal!</h2><br/>
        <p>Feel free to click the option dropdown menu in the navigation bar above. From there you can navigate to your projects tab or your client meetings tab.</p>
    </div>
    <router-outlet (activate)="checkChild()"></router-outlet>
</body>
