import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';
import { Router } from '@angular/router';
import { MeetingscheduleService } from '../services/meetingschedule.service';
import { Meetingschedule } from '../classes/meetingschedule';
import { MeetingrequestService } from '../services/meetingrequest.service';
import { Meetingrequest } from '../classes/meetingrequest';

@Component({
  selector: 'app-admin-meetings',
  templateUrl: './admin-meetings.component.html',
  styleUrls: ['./admin-meetings.component.css']
})
export class AdminMeetingsComponent implements OnInit {
  newRef = new FormGroup({
    name:new FormControl(),
    email:new FormControl(),
    participants:new FormControl(),
    date:new FormControl(),
    topic:new FormControl()
  });

  meetingschedule:Array<Meetingschedule> = [];
  meetingrequests:Array<Meetingrequest> = [];
  displayTag:boolean = true;
  formTag:boolean = false;

  constructor(public meetingService:MeetingscheduleService,
              public requestService:MeetingrequestService,
              public router:Router) {}

  ngOnInit(): void {
    this.meetingService.loadSchedule().subscribe({
      next:(result:any)=> {
        this.meetingschedule = result;
      },
      error:(error:any)=> {
        console.log(error);
      },
      complete:()=> {
        console.log("done!");
      }
    });
    this.requestService.loadRequestList().subscribe({
      next:(result:any)=> {
        this.meetingrequests = result;
      },
      error:(error:any)=> {
        console.log(error);
      },
      complete:()=> {
        console.log("done!");
      }
    });
  }

  displayForm():void {
    this.formTag = true;
    this.displayTag = false;
  }

  hideForm():void {
    this.formTag = false;
    this.displayTag = true;
  }

  newMeeting():void {
    let newmeeting = this.newRef.value;
    this.meetingService.storeNewMeeting(newmeeting).subscribe({
      next:(result:any)=> {
        this.meetingschedule = result;
      },
      error:(error:any)=> {
        console.log(error);
      },
      complete:()=> {
        alert("Meeting Added!");
        console.log("done!");
      }
    });
    this.newRef.reset();
  }
}
<html>
    <head>
        <!-- Bootstrap Implementation -->
        <link href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.2/dist/css/bootstrap.min.css" rel="stylesheet" integrity="sha384-T3c6CoIi6uLrA9TneNEoa7RxnatzjcDSCmG1MXxSR1GAsXEV/Dwwykc2MPK8M2HN" crossorigin="anonymous"/>
        <meta name="viewport" content="width=device-width, initial-scale=1">
    </head>
    <body>
    <div *ngIf="displayTag">
        <div align="center">
        <h3>Client Meetings</h3>
        <input type="button" value="Add New Meeting" (click)="displayForm()">
        </div>

        <br/><h3>Meeting Schedule</h3><br/>
        <div align="center">
            <table class="custom">
                <tr>
                    <th>Name</th>
                    <th>Email</th>
                    <th># of Participants</th>
                    <th>Meeting Date</th>
                    <th>Topic</th>
                </tr>
                <tr *ngFor="let each of meetingschedule">
                    <td>{{each.name}}</td>
                    <td>{{each.email}}</td>
                    <td>{{each.participants}}</td>
                    <td>{{each.date}}</td>
                    <td>{{each.topic}}</td>
                </tr>
            </table>
        </div>
        <br/><h3>Meeting Requests</h3><br/>
        <div align="center">
            <table class="custom">
                <tr>
                    <th>Name</th>
                    <th>Email</th>
                    <th># of Participants</th>
                    <th>Meeting Date</th>
                    <th>Topic</th>
                </tr>
                <tr *ngFor="let each of meetingrequests">
                    <td>{{each.name}}</td>
                    <td>{{each.email}}</td>
                    <td>{{each.participants}}</td>
                    <td>{{each.date}}</td>
                    <td>{{each.topic}}</td>
                </tr>
            </table>
            <br/>

        </div>
    </div>

        <div *ngIf="formTag" align="center">
            <br/>
            <h4 style="color:white">Schedule New Meeting</h4>
            <form [formGroup]="newRef" (ngSubmit)="newMeeting()">
                <label for="name">Client Name</label><br/>
                <input type="text" formControlName="name" required><br/>
                <label for="email">Client Email</label><br/>
                <input type="text" formControlName="email" required><br/>
                <label for="participants">Number of Participants</label><br/>
                <input type="text" formControlName="participants" required><br/>
                <label for="date">Date and Time</label><br/>
                <input type="text" formControlName="date" required><br/>
                <label for="topic">Meeting Topic</label><br/>
                <input type="text" formControlName="topic" required><br/>
                <input class="btn btn-success" type="submit" value="Submit"> 
                <button class="btn btn-warning" (click)="hideForm()">Cancel</button>
            </form>
        </div>
    </body>
</html>
