import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';
import { Router } from '@angular/router';
import { AdminprojectService } from '../services/adminproject.service';
import { Adminproject } from '../classes/adminproject';
import { delay } from 'rxjs';

@Component({
  selector: 'app-admin-projects',
  templateUrl: './admin-projects.component.html',
  styleUrls: ['./admin-projects.component.css']
})
export class AdminProjectsComponent implements OnInit{
  projRef = new FormGroup({
    id:new FormControl(),
    pname:new FormControl(),
    due:new FormControl(),
    description:new FormControl()
  });

  projects:Array<Adminproject>=[];
  displayTag:boolean = true;
  formTag:boolean = false;
  buttonVariable = "Submit";
  constructor(public projectService:AdminprojectService,
              public router:Router) {}

  ngOnInit():void {
    this.projectService.loadAllProjects().subscribe({
      next:(result:any)=> {
        this.projects = result;
      },
      error:(error:any)=> {
        console.log(error);
      },
      complete:()=> {
        console.log("done!");
      }
    });
  }

  displayForm():void {
    this.displayTag = false;
    this.formTag = true;
  }

  hideForm():void {
    this.displayTag = true;
    this.formTag = false;
  }

  newProj():void {
    let newproject = this.projRef.value;

    if (this.buttonVariable == "Submit") {
      this.projectService.storeNewProject(newproject).subscribe({
        next:(result:any)=> {
          this.projects = result;
        },
        error:(error:any)=> {
          console.log(error);
        },
        complete:()=> {
          alert("Project Added!");
          console.log("done!");
          this.ngOnInit();
          this.formTag = false;
          this.displayTag = true;
        }
        });
        this.projRef.reset();
    }
    else {
      this.projectService.updateProj(newproject).subscribe({
        next:(result:any)=> {
          this.projects = result;
        },
        error:(error:any)=> {
          console.log(error);
        },
        complete:()=> {
          alert("Project Updated!");
          console.log("done!");
          this.ngOnInit();
          this.formTag = false;
          this.displayTag = true;
        }
        });
        this.projRef.reset();
    }

 }

 deleteProject(id:any):void {
  this.projectService.deleteProj(id).subscribe({
    next:(result:any)=> {
      this.projects = result;
    },
    error:(error:any)=> {
      console.log(error);
    },
    complete:()=> {
      alert("Project Deleted!");
      console.log("done!");
      this.ngOnInit();
    }
    });
 }

 updateProject(project:any):void {
  this.projRef.get("id")?.setValue(project.id);
  this.projRef.get("pname")?.setValue(project.pname);
  this.projRef.get("due")?.setValue(project.due);
  this.projRef.get("description")?.setValue(project.description);
  this.formTag = true;
  this.displayTag = false;
  this.buttonVariable = "Update";
 }

}
<html>
    <head>
        <!-- Bootstrap Implementation -->
        <link href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.2/dist/css/bootstrap.min.css" rel="stylesheet" integrity="sha384-T3c6CoIi6uLrA9TneNEoa7RxnatzjcDSCmG1MXxSR1GAsXEV/Dwwykc2MPK8M2HN" crossorigin="anonymous"/>
        <meta name="viewport" content="width=device-width, initial-scale=1">
    </head>
<body>
    <div *ngIf="displayTag">
        <div align="center">
        <h3>Projects</h3>
        <input type="button" value="Add New Project" (click)="displayForm()">
        </div><br/><br/>

    <div align="center">
        <table class="custom">
            <tr>
                <th>Id</th>
                <th>Name</th>
                <th>Due Date</th>
                <th>Description</th>
                <th>Delete</th>
                <th>Update</th>
            </tr>
            <tr *ngFor="let project of projects">
                <td>{{project.id}}</td>
                <td>{{project.pname}}</td>
                <td>{{project.due}}</td>
                <td>{{project.description}}</td>
                <td><input type="button" value="Delete" (click)="deleteProject(project.id)"/></td>
                <td><input type="button" value="Update" (click)="updateProject(project)"/></td>
            </tr>
        </table>
    </div>
</div>

    <div *ngIf="formTag" align="center">
        <br/>
        <h4 style="color: white;">Add New Project</h4>
        <form [formGroup]="projRef" (ngSubmit)="newProj()">
            <label for="pname">Project Name</label><br/>
            <input type="text" formControlName="pname" required><br/>
            <label for="due">Due Date</label><br/>
            <input type="text" formControlName="due" required><br/>
            <label for="description">Project Description</label><br/>
            <input type="text" formControlName="description" required><br/>
            <input class="btn btn-success" type="submit" [value]="buttonVariable"> 
            <button type="reset" class="btn btn-warning">Clear</button><br/>
            <button type="button" class="btn btn-danger" (click)="hideForm()">Cancel</button>
        </form>
    </div>
</body>
</html>
