import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { FormGroup, FormBuilder } from '@angular/forms';
import { FormControl } from '@angular/forms';
import { ClientregisterService } from '../services/clientregister.service';
import { Clientregister } from '../classes/clientregister';

@Component({
  selector: 'app-client-register',
  templateUrl: './client-register.component.html',
  styleUrls: ['./client-register.component.css']
})
export class ClientRegisterComponent implements OnInit{
  registerRef = new FormGroup ({
    name:new FormControl(),
    email:new FormControl(),
    password:new FormControl()
  });

  alluserinfo:Array<Clientregister> = [];

  constructor(public clientregisterService:ClientregisterService,
              public router:Router,
              ) {}

  storenewUser():void {
    let newuserdetails = this.registerRef.value;
    this.clientregisterService.storenewUserInfo(newuserdetails).subcribe({
      next:(result:any)=> {
        this.alluserinfo = result;
      },
      error:(error:any)=> {
        console.log(error);
      },
      complete:()=> {
        alert("You have been registered... Please log in!");
        console.log("done!");
      }
    });
    this.registerRef.reset();
  }
}
<html>
<head>
    <!-- Bootstrap Implementation -->
    <link href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.2/dist/css/bootstrap.min.css" rel="stylesheet" integrity="sha384-T3c6CoIi6uLrA9TneNEoa7RxnatzjcDSCmG1MXxSR1GAsXEV/Dwwykc2MPK8M2HN" crossorigin="anonymous"/>
    <meta name="viewport" content="width=device-width, initial-scale=1">
</head>
<body>
<br/><div>
    <h3>Welcome, User!</h3><br/>
    <p class="smallText">Please register your credentials below:<p>
    <form [formGroup]="registerRef" (ngSubmit)="storenewUser()">
        <input type="text" formControlName="name" placeholder="Full Name" required><br/>
        <input type="email" formControlName="email" placeholder="Email" required><br/>
        <input type="password" formControlName="password" placeholder="Password" required><br/>
        <input type="submit" value="Register" class="btn btn-primary"/><br/>
    </form>
</div>
</body>
</html>
