import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { FormControl } from '@angular/forms';
import { Router } from '@angular/router';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-client-login',
  templateUrl: './client-login.component.html',
  styleUrls: ['./client-login.component.css']
})
export class ClientLoginComponent implements OnInit{

  loginRef = new FormGroup({
    email:new FormControl(),
    password:new FormControl()
  });

  constructor(public router:Router,
              public formbuilder:FormBuilder,
              public http:HttpClient) {}

  ngOnInit(): void {
    this.loginRef = this.formbuilder.group({
      email: [''],
      password: ['']
    });
  }

  checkUser() {
    let login = this.loginRef.value;
    this.http.get<any>("http://localhost:3000/alluserinfo").subscribe(result=>{
      const user = result.find((a:any)=> {
        return a.email == login.email && a.password == login.password
      });
      if (user) {
        alert("Login Successful... Welcome!");
        this.loginRef.reset();
        this.router.navigate(["/client-dashboard"], {skipLocationChange:true});
      }
      else {
        alert("Login Failed... Please try again!");
        this.loginRef.reset();
      }
    }, err=> {
      alert("Something went wrong!");
    });
  }
}
<html>
<head>
    <!-- Bootstrap Implementation -->
    <link href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.2/dist/css/bootstrap.min.css" rel="stylesheet" integrity="sha384-T3c6CoIi6uLrA9TneNEoa7RxnatzjcDSCmG1MXxSR1GAsXEV/Dwwykc2MPK8M2HN" crossorigin="anonymous"/>
    <meta name="viewport" content="width=device-width, initial-scale=1">
</head>
<body>
<br/><div>
    <h3>Welcome back, Client!</h3><br/>
    <p class="smallText">Please enter your credentials below:<p>
    <form [formGroup]="loginRef" (ngSubmit)="checkUser()">
        <input type="email" formControlName="email" placeholder="Email" required><br/>
        <input type="password" formControlName="password" placeholder="Password" required><br/>
        <input type="submit" value="Sign In" class="btn btn-primary">
    </form>
    <br/>
    <p class="smallText" id="1">Don't have an account?<br/>
        Click "Register" in the navigation bar above!</p>
</div>
</body>
</html>
